using System;
using System.Collections.Generic;
using System.Linq;

namespace _3Sum
{
    /*
    Given an integer array nums, return all the triplets [nums[i], nums[j], nums[k]] 
    such that i != j, i != k, and j != k, and nums[i] + nums[j] + nums[k] == 0.
    Notice that the solution set must not contain duplicate triplets.

    Example 1:

    Input: nums = [-1,0,1,2,-1,-4]
    Output: [[-1,-1,2],[-1,0,1]]
    Example 2:

    Input: nums = []
    Output: []
    Example 3:

    Input: nums = [0]
    Output: []
 

    Constraints:

        0 <= nums.length <= 3000
        -105 <= nums[i] <= 105
    */

    internal class Program
    {



        private static void Main(string[] args)
        {
            /*
        Input:
        [-1,0,1,2,-1,-4,-2,-3,3,0,4]
        Output:
        [[-1,0,1],[-1,-1,2],[-2,-1,3],[-3,-1,4],[-3,0,3],[-4,1,3]]
        Expected:
        [[-4,0,4],[-4,1,3],[-3,-1,4],[-3,0,3],[-3,1,2],[-2,-1,3],[-2,0,2],[-1,-1,2],[-1,0,1]]
        */

            var nums = new int[] { -1, 0, 1, 2, -1, -4, -2, -3, 3, 0, 4 };

            var expected = new List<IList<int>>();
            expected.Add(new List<int> { -4, 0, 4 });
            expected.Add(new List<int> { -4, 1, 3 });
            expected.Add(new List<int> { -3, -1, 4 });
            expected.Add(new List<int> { -3, 0, 3 });
            expected.Add(new List<int> { -3, 1, 2 });
            expected.Add(new List<int> { -2, -1, 3 });
            expected.Add(new List<int> { -2, 0, 2 });
            expected.Add(new List<int> { -1, -1, 2 });
            expected.Add(new List<int> { -1, 0, 1 });

            var output = Solution.ThreeSum(nums);
            Console.WriteLine(CheckOutput(output, expected));

            /*
                Scenario 2:

                Input:
                [0,0,0]
                Expected:
                [[0,0,0]]
            */

            nums = new int[] { 0, 0, 0 };
            expected = new List<IList<int>>();
            expected.Add(new List<int> { 0, 0, 0 });
            output = Solution.ThreeSum(nums);
            Console.WriteLine(CheckOutput(output, expected));


