        // check if the output is correct
        public static bool CheckOutput(IList<IList<int>> output, IList<IList<int>> expectedOutput)
        {
            if (output.Count != expectedOutput.Count)
            {
                return false;
            }

            foreach (var list in output)
            {
                if (!expectedOutput.Any(ex => ex.SequenceEqual(list)))
                {
                    return false;
                }
            }
            return true;
        }
    }
    
